function user_topics = assign_topics()  % load data  tweets = load('tweets.txt');  topics = load('topics.txt');  % topic assignment based on jaccard similarity  num_users = size(tweets,1);  num_topics = size(topics,1);  num_words = size(topics,2);  user_topics = zeros(num_users, num_topics);  for i=1:num_users    t = tweets(i,2:end);    jaccard_sim = sum(t&topics,2)/num_words;    user_topics(i,:) = jaccard_sim';  end  % scaling  user_topics = user_topics./sum(user_topics);  end