function dist=calcGeodistance(latlon1, latlon2)    ct = pi / 180;    radius = 6371;            % calc haversine distance     lat1=latlon1(1)*ct;    lat2=latlon2(1)*ct;    lon1=latlon1(2)*ct;    lon2=latlon2(2)*ct;    deltaLat=lat2-lat1;    deltaLon=lon2-lon1;    a=sin((deltaLat)/2)^2 + cos(lat1)*cos(lat2) * sin(deltaLon/2)^2;    c=2*atan2(sqrt(a),sqrt(1-a));    d1km=radius*c;    %Haversine distance    x=deltaLon*cos((lat1+lat2)/2);    y=deltaLat;    dist=radius*sqrt(x^2 + y^2); %Pythagoran distance        end