function [distance,assignment,centroids] = calcKMeans(docs, k)  num_words = size(docs,2);  num_docs = size(docs,1);    % random initialization  centroids = docs(randi(num_docs, k, 1),:);    for iteration=1:50    % find closest centroid    distance = ones(num_docs, k);    for i=1:k      distance(:,i) =  sum( (docs - repmat(centroids(i,:),size(docs,1),1)).^2 , 2);    end    [dist, assignment] = min(distance,[],2);        % compute new mean for centroids    for i=1:k						total = sum(assignment==i);						if total == 0				continue;			end			      centroids(i,:) =  sum(docs(assignment ==i,:),1)/total;    end  end  end