clear ; close all; clc% users (id, tweets, friends, followers)num_users = 100;users = [[1:num_users]', randi(100, num_users,3)];csvwrite('users.txt',users);% linkslinks_filename = "links.txt";delete (links_filename);links = rand(num_users) > 0.8;for i=1:num_users  for j=1:num_users    if links(j,i) == 1      link = [i,j];      csvwrite(links_filename, link, "-append");    end  endend% tweetstweets_filename = "tweets.txt";delete (tweets_filename);num_vocab = 100;max_tf = 25;for i=1:num_users  for j=1:num_vocab    if rand() > 0.7      tf = randi(max_tf);      bag = [i, j, tf];      csvwrite(tweets_filename, bag, "-append");    end  endend% locationtweets_filename = "locations.txt";delete (tweets_filename);num_tweets = 1;for i=1:num_users  for j=1:num_tweets        if rand() > 0.5      long = -2.149636 + rand();    else      long = -2.149636 - rand();    end        if rand() > 0.5      lat = -79.907823 - rand();    else      lat = -79.907823 + rand();    end        loc = [i, j, long, lat];    csvwrite(tweets_filename, loc, "-append");  endend% retweets% favourites% mentions% replies% attributions