function t = loadTweets(filename)  fprintf('loading tweets...');  tweets = load(filename);  users = unique(tweets(:,1));  words = unique(tweets(:,2));  num_users= length(users);  num_words = length(words);  num_rows = size(tweets, 1);  t = zeros(num_users, num_words);    % fill matrix (users) x (words)  for i=1:num_rows    t(tweets(i,1), tweets(i,2) )=tweets(i,3);    printProgress(i,num_rows);  end  fprintf('\nloading tweets...[ok]\n');end